// This node generates sensors that contain the diagnostic values for the device (not the individual sensors)

function capitolizeFirst(string) {
 var output = string.replace(/\w\S*/g, function(txt){return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();});
 return output;
}
msg.payload = ""
    for (const property in msg.thing.configuration) {
        baseTopic  =                    global.get("mqttDiscoveryTopic") + "/sensor/" + msg.thing.hass.UID_ha + "/" + property
        msg.topic =  baseTopic + "/config"

        switch(property){
            case "deviceType":
                name = msg.thing.label + " Device Type"
                break;
            case "deviceInstance":
                name = msg.thing.label + " Device Instance"
                break;
            case "capability":
                name = msg.thing.label + " Capability"
                break;
            case "productID":
                name = msg.thing.label + " Product ID"
                break;
            case "functionName":
                name = msg.thing.label + " Function Name"
                break;
            case "functionInstance":
                name = msg.thing.label + " Function Instance"
                break;
            case "mac":
                name = msg.thing.label + " MAC Address"
                break;
            default:
                name = property
                break;
        }

        msg.payload = {
            "name":                 name,
            "object_id":            global.get("namePrecursor") + name,
            "unique_id":            msg.thing.hass.UID_ha + "_" + property,
            "entity_category":      "diagnostic",
            "state_topic":          baseTopic + "/state",
            "availability":         {"topic":                       msg.thing.hass.availabilityTopic,
                                    	"payload_available":        msg.thing.hass.availabilityTopicOn,
                                    	"payload_not_available":    msg.thing.hass.availabilityTopicOff,
                                    	"value_template":           "{{ value_json." + msg.thing.hass.availabilityTopicAttr + " }}", 
                                        },
            "device":               {"manufacturer":       global.get("manufacturer"), 
                                        "via_device":       global.get("via_device"),
                                        "identifiers":      msg.thing.hass.UID_ha, 
                                        "name":             msg.thing.label, 
                                        "model":            global.get("model") + ": " + msg.thing.hass.UID_ha,
                                        "sw_version":       global.get("sw_version")
                                    },

            "enabled_by_default":   true

        } 

      node.send(msg)
      // Send the default values from the config to the MQTT topic for the attribute above
      msg1 = {}
      msg1.topic = baseTopic + "/state"
      msg1.payload = msg.thing.configuration[property]
      node.send(msg1)
}

function capitolizeFirst(string) {
 var output = string.replace(/\w\S*/g, function(txt){return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();});
 return output;
}
var msg1 = {}
msg1.debug = {}
var channel = {}

channelRaw =                msg.payload
//msg.payload =               {}
channelUID =                channelRaw.uid.replace("idsmyrv:","")
channelUID =                channelUID.replace("-thing:","_")
channelUID =                channelUID.replace(/-/g,"_")
channelTypeUID =            channelRaw.channelTypeUID.replace("idsmyrv:","")
channelUID_ha =             channelRaw.uid.replace(/:/g,"_")
channelUID_ha =             channelUID_ha.replace(/-/g,"_")

msg.payload.hass = {}

msg.payload.hass = {
    "label_ha":                     msg.thing.hass.label + " " + capitolizeFirst(channelRaw.id),
    "channelUID_oc":                channelRaw.uid,
    "channelUID_ha":                channelUID_ha,
    "id_oc":                        channelRaw.id,
    "valueType_oc":                 channelRaw.itemType.toLowerCase(),
    "channelType_ha":               msg.thing.hass.thingTypeUID_ha +":"+channelRaw.id,
    "kind":                         channelRaw.kind.toLowerCase(),
    "topicItemStateEvent":          "smarthome/items/" + channelUID_ha + "/state",
    "topicThingStatusInfoEvent":    "smarthome/things/" + msg.thing.UID + "/status",
    "topicItemStateChangedEvent":   "smarthome/items/" + channelUID_ha + "/statechanged",
    "topicItemCommandEvent":        "smarthome/items/" + channelUID_ha + "/command",
    "topicItemCommandSend":         "smarthome/items/" + channelUID_ha + "/set"
                            }  

// Build base discovery content 


basePayload =                       {"name":                    msg.payload.hass.label_ha,
                                    "object_id":                global.get("namePrecursor") + msg.payload.hass.label_ha,
                                    "unique_id":                msg.payload.hass.channelUID_ha,
                                    "state_topic":              msg.payload.hass.topicItemStateEvent,
                                    "value_template":           "{{ value_json.value }}", 
                                    "availability": {
                                    	"topic":                    msg.thing.hass.availabilityTopic,
                                    	"payload_available":        msg.thing.hass.availabilityTopicOn,
                                    	"payload_not_available":    msg.thing.hass.availabilityTopicOff,
                                    	"value_template":           "{{ value_json." + msg.thing.hass.availabilityTopicAttr + " }}", 
                                        },
                                    "device": {
                                        "manufacturer":         global.get("manufacturer"),
                                        "via_device":           global.get("via_device"),
                                        "identifiers":          msg.thing.hass.UID_ha,
                                        "name":                 msg.thing.hass.label,
                                        //"connections":          [["mac", msg.thing.configuration.mac],["foo","bar"]],
                                        "model":                global.get("model") + ": " + msg.thing.hass.UID_ha,
                                        "sw_version":           global.get("sw_version")
                                        }
    }


//var channel = {} // Used to add entity-specific attributes to the payload

//Set generic entity types based on OpenHab channel itemTypes 
switch(msg.payload.hass.channelType_ha){
    case "light:dimmer":
        msg.payload.hass.entityType_ha = "light"
        break;
    case "switched-light:switch":
        msg.payload.hass.entityType_ha = "light"
        break;
    default:
        msg.payload.hass.entityType_ha = "sensor"
        break;
}

msg.payload.hass.discovery = {}
msg.payload.hass.attributes = {}
msg.payload.hass.discovery.payload = basePayload
msg.payload.hass.attributes.payload = msg.thing.configuration
return [msg,msg]

addPayload = {}

if (msg.payload.hass.channelType_ha.includes(":") === true ) {
    channel = msg.payload.hass.channelType_ha.split(":")
    channelType = channel[1]
} else {
    channel = msg.payload.hass.channelType_ha
}

discoveryPayload = msg.payload.hass.discovery.payload

// Add device additional details as needed based on channel types
if (channelType.includes("voltage") === true ) {
        addPayload = {
            "device_class":                 "voltage",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("current") === true ) {
        addPayload = {
            "device_class":                 "current",
            "unit_of_measurement":          "A",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("dimmer") === true ) {
        baseTopic_oc = "smarthome/items/" + msg.payload.hass.channelUID_ha
        baseName = msg.thing.hass.UID_oc.replace(/:/g,"_")
        baseName = "smarthome/items/" + baseName.replace(/-/,"_")
        delete msg.payload.hass.discovery.payload.value_template

        addPayload = {
            "state_topic":                  baseName + "_switch/state",
            "command_topic":                baseTopic_oc + "/set",
            "brightness_state_topic":       baseName + "_brightness/state",
            "brightness_command_topic":     baseTopic_oc + "/set",
            "payload_on":                   "on",
            "payload_off":                  "off",
            "brightness_scale":             100,
            "on_command_type":              "last",
            "qos":                          0,
            "effect_state_topic":           baseName + "_mode/state",
            "effect_command_topic":         baseName + "_mode/set",
            "effect_list":                  ["off","dimmer","blink","swell"],
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("fault") === true) {
        msg.payload.hass.entityType_ha = "binary_sensor"
        addPayload = {
            "device_class":                 "problem",
            "payload_on":                   "on",
            "payload_off":                  "off",
            "entity_category":              "diagnostic",
            "enabled_by_default":           "true"
            }
    }


if (msg.payload.hass.channelType_ha.includes("generator:command") === true) {
        msg.payload.hass.entityType_ha =    "switch"
        msg.payload.hass.discovery.payload.name = "Generator Switch"
        msg.payload.hass.discovery.payload.object_id = global.get("namePrecursor") + msg.payload.hass.discovery.payload.name
        addPayload = {
            "command_topic":                msg.payload.hass.topicItemCommandSend,
            "payload_on":                   "on",
            "payload_off":                  "off",
            "state_on":                     "on",
            "state_off":                    "off",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("output-disable") === true) {
        msg.payload.hass.entityType_ha = "sensor"
        addPayload = {
            "entity_category":              "diagnostic",
            "enabled_by_default":           "true"
            }
    }


if (channelType.includes("online") === true ) {
        msg.payload.hass.entityType_ha =        "binary_sensor"
        addPayload = {
            "device_class":                 "connectivity",
            "payload_on":                   "on",
            "payload_off":                  "off",
            "entity_category":              "diagnostic",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("output-disable-cause") === true ) {
        addPayload = {
            "entity_category":              "diagnostic",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("percent") === true ) {
    if (msg.payload.hass.id_oc == "position") {
            addPayload = {
                "unit_of_measurement":          '%',
                "value_template":               '{{ (((value_json |float)/255)*100)|round(0) }}',
                "enabled_by_default":           "true"
                }
            } else {
                addPayload = {
                "enabled_by_default":           "true"
                }
            }
    }

if (channelType.includes("sleep") === true) {
        msg.payload.hass.entityType_ha =        "number"
        addPayload = {
            "command_topic":                msg.payload.hass.topicItemCommandSend,
            "qos":                          0,
            "unit_of_measurement":          "mins.",
            "step":                         1,
            "min":                          0,
            "max":                          120,
            "icon":                         "mdi:timer",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("switch") === true &&
    channelType.includes("switched-light") != true) {
        msg.payload.hass.entityType_ha =    "switch"
        addPayload = {
            "command_topic":                msg.payload.hass.topicItemCommandSend,
            "payload_on":                   "on",
            "payload_off":                  "off",
            "state_on":                     "on",
            "state_off":                    "off",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("switched-light") === true) {
        msg.payload.hass.entityType_ha =        "light"
        addPayload = {
            "command_topic":                msg.payload.hass.topicItemCommandSend,
            "payload_on":                   "on",
            "payload_off":                  "off",
            "state_on":                     "on",
            "state_off":                    "off",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("tank-level") === true ) {
        addPayload = {
            "icon":                         "mdi:water-percent",
            "unit_of_measurement":          "%",
            "enabled_by_default":           "true"
                }
    }


if (channelType.includes("temperature") === true && 
    channelType.includes("temperature-setpoints") === false) {
        addPayload = {
            "device_class":                 "temperature",
            "unit_of_measurement":          "Â°F",
            "enabled_by_default":           "true"
            }
    }

if (channelType.includes("temperature-setpoints") === true) {
        addPayload = {
            "enabled_by_default":           "false"
            }
    }

// These sensors are unknown in their purpose, so we create them, but do not enable them unless we want to.
if (channelType.includes("time1") === true || channelType.includes("time2") === true ) {
        addPayload = {
            "enabled_by_default":           "false"
                }
    }


discoveryBaseTopic = global.get("mqttDiscoveryTopic") + "/" + msg.payload.hass.entityType_ha + "/" + msg.thing.hass.UID_ha + "/" + msg.payload.hass.id_oc
msg.payload.hass.discovery.topic = discoveryBaseTopic + "/config"
msg.payload.hass.attributes.topic = discoveryBaseTopic + "/attributes"
msg.payload.hass.discovery.payload = {...discoveryPayload,...addPayload }

msg1 = {}
msg1.topic = msg.payload.hass.discovery.topic
msg1.payload = msg.payload.hass.discovery.payload
return [msg1,msg];

// ####################### Begin Online Status Details sensor #######################
// OneControl provides additional information in the availability messages published, and this informaion
//   is very useful in diagnosing potential connection issues.
// This sensor displays any additional status details if there are any to report
name = msg.thing.label + " Online Status Details"
property = "online_status_details"
baseTopic  =  global.get("mqttDiscoveryTopic") + "/sensor/" + msg.thing.hass.UID_ha + "/" + property
msg.topic =  baseTopic + "/config"

msg.payload = {
    "name":                 name,
    "object_id":            global.get("namePrecursor") + name,
    "unique_id":            msg.thing.hass.UID_ha + " " + property,
    "entity_category":      "diagnostic",
    "state_topic":          msg.thing.hass.availabilityTopic,
    "value_template":       "{{ value_json.statusDetail }}",
    "availability":         {"topic":                      msg.thing.hass.availabilityTopic,
                            	"payload_available":        msg.thing.hass.availabilityTopicOn,
                            	"payload_not_available":    msg.thing.hass.availabilityTopicOff,
                            	"value_template":           "{{ value_json." + msg.thing.hass.availabilityTopicAttr + " }}", 
                                },
    "device":                {"manufacturer":       global.get("manufacturer"), 
                                "via_device":       global.get("via_device"),
                                "identifiers":      msg.thing.hass.UID_ha, 
                                "name":             msg.thing.label, 
                                "model":            global.get("model") + ": " + msg.thing.hass.UID_ha,
                                "sw_version":       global.get("sw_version")
                                },
    "enabled_by_default":   true
    } 
node.send(msg)
// ####################### End Online Status Details sensor #######################


// ####################### Begin Startup Availability Message Publishing #######################
// This section is used by the 'startupAvailMsg' option.  When this is set to true, this code will
//   send a dummy available payload to trigger the availability for the entities created in this tool.
 if (global.get("mqttInitialAvail") === true) {
     msg.payload = '{"status":"online","statusDetail":"Initial Discovery"}'
     msg.topic = msg.thing.hass.availabilityTopic
     msg.retain = true
     node.send(msg)
 }
 // ####################### End Startup Availability Message Publishing #######################

function capitolizeFirst(string) {
 var output = string.replace(/\w\S*/g, function(txt){return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();});
 return output;
}

if (typeof msg.payload.name !== 'undefined') {
    if (msg.payload.name.includes("-") === true ){
        msg.payload.name = msg.payload.name.replace("-"," ")
         msg.payload.name = capitolizeFirst(msg.payload.name)
    }
}
return msg;

/**
 OneControl Auto Discovery into Home Assistant

 This process will integrate your Lippert OneControl components into Home Assistance
	(http://www.home-assistant.io)

 Use this file to set the bahavior of the process of pulling devices from the OneControl system
*/


// You can use this tool for many purposes, and if you are conservative in your approach, you would like to review 
//   the code before connecting it to your environment.
// By default, the monitoring flow connects to the OneControl cloud gateway as soon as the flow is deployed.  This ensures
//   that every time a change is made and deployed the connection to the event feed is re-established automatically.  A trigger
//   node is available for manually kicking off the connection for manual control.
//     default:
//          eventsGetStream = true
eventsGetStream = false;

// As this tool starts, it subscribes to the MQTT topics that are used for the processing of commands and events, 
//   and if a command is meant to be sent to OneControl, it will do so automatically.  If you are working to troubleshoot 
//   or review what is happening before sending it to OneControl, you will likely want to prevent this from occuring until you
//   prepared for it to be done.
// Note: This will disable the actual transmission to OneControl, but NOT the event processing.  This will allow you to see what would
//   be sent if/once it is enabled.
//     default:
//          eventsSendStream = true
eventsSendStream = true;


// When the information is pulled from the OneControl system, you have the option to write the information
//   to files on the local system, as both YAML and JSON.  These can be used to help troubleshoot, or you can 
//   us the YAML output as the config files for Home Assistant. Set to "true" to have the output files written
//   to disk.  If files exist, they will be overwritten.
// ** NOTE ** These files are NOT needed for this process to work, and are for troubleshooting/stand alone use.
//     default:
//          filesCreate = false
filesCreate = true;

// This is where you define the destination for the files if you are using the tool 
//     default: 
//          filesSaveTo = '/config/OneControl/'
filesSaveTo = '/config/OneControl/';

// When files are saved, they are saved as YAML and JSON, but include the attributes needed for Discovery, and
//   cannot be used for static YAML configuration without certain sections removed. Set this to true to remove
//   the discovery-related sections.
// ** NOTE ** 
// You will still need to use the "OneControl - Command and Event Stream" flow to process events to align with the 
//   MQTT topics that are part of the configuration files
//     default:
//          filesNoDiscovery = false
filesNoDiscovery = false;

// If you do not want to send the configuration to the MQTT server, set this to "false"
//     default: 
//          mqttPublish = false
mqttPublish = true;

// If you want to have MQTT discovery messages retained between restarts, set this option to "true"
// If you do not set this to true, your Home Assistant OneControl entities will be in an error state until you
//   run the discovery script again.  This is due to how Home Assistant stores entity information.
//     default: 
//          mqttRetain = true
mqttRetain = true;

// When you initially create the entities in Home Assistant, it can take a long
//   time before an availability message is published from OneControl, sometimes 
//   upwards of 30 minutes.  This is because OneControl publishes changes in real
//   time, but non-change status messages much less frequently.  This can be frustrating
//   when initially creating the entities.
// Enable this setting to generate an "online" message when you do the initial 
//   installation, which will show the entitied as "online" immediately.  If the
//   entities are truly offline, you will not be able to run the discovery process
//   without an error, so enabling this is low (but not no) risk.  Please note
//   that this will only occur once at the time the discovery process is run.
// If you enable this, the device's "Online Status Details" sensor value will be
//   'Initial Discovery' until OneControl publishes an official message.  It is not uncommon
//   to see the online entity listed as "Disconnected" until this first message is received.
//     default: 
//          mqttInitialAvail = true
mqttInitialAvail = true;

// Often when you make changes, you may need to remove all the entities created.
//   This option will enable the process to remove all entities created in previous
//   run AS LONG AS NO OTHER SETTINGS HAVE CHANGED (this depends on the deiscovery
//   topic to be the same).
//   Note: sendToMQTT must also be enabled
//     default:
//          mqttDeleteAll = false;
mqttDeleteAll = false;

// Home Assistant requires the definition of the topic used for discovery.  
// For more info on what is needed, review the Home Assistant documentation here:
//    https://www.home-assistant.io/docs/mqtt/discovery/
// This value needs to match what is entered into your configuration.yaml file
// configuration.yaml example:
//     mqtt:
//          discovery:          true
//          discovery_prefix:   'homeassistant'
//
//     default:
//          mqttDiscoveryTopic = 'homeassistant'
mqttDiscoveryTopic = 'homeassistant';

// Part of the discovery output is the definition of the manufacturer the component.
//     default: 
//          manufacturer = 'Lippert Components'
manufacturer = 'Lippert Components';

// Part of the discovery output is the definition of the device that is used to 
//   connect to the individual nodes.
//     default:
//          via_device = 'OneControl Gateway';
via_device = 'OneControl Gateway';

// Part of the discovery output is the definition of the model the component.
//     default:
//          model = 'OneControl';
model = 'OneControl';

// when the Home Assistant entity is created, you can prepend this string to the
//   entityID: 'sensor.example' would become 'sensor.namePresursor_example'
//     default:
//          namePrecursor= 'OneControl '
namePrecursor = 'OneControl ';



/* ############ Do not change anything below this line ############ */
Release_Version = '2022.01.12a'

global.set("eventsGetStream",eventsGetStream);
global.set("eventsSendStream",eventsSendStream);

global.set("filesCreate",filesCreate);
global.set("filesSaveTo",filesSaveTo);
global.set("filesNoDiscovery",filesNoDiscovery);

global.set("mqttPublish",mqttPublish);
global.set("mqttRetain",mqttRetain);
global.set("mqttDeleteAll",mqttDeleteAll);
global.set("mqttInitialAvail",mqttInitialAvail);


global.set("mqttDiscoveryTopic",mqttDiscoveryTopic);
global.set("sw_version",Release_Version);
global.set("manufacturer",manufacturer);
global.set("via_device",via_device);
global.set("model",model);
global.set("namePrecursor",namePrecursor);


return msg;
